import React from 'react';
import Todo from './Todo';
import AddTodo from './AddTodo';
import { Paper, List, Container, Typography, Grid } from "@mui/material";
import './App.css';
import { v4 as uuidv4 } from 'uuid';
import axios from 'axios';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [
        { id: uuidv4(), title: "Todo 1", done: true },
        { id: uuidv4(), title: "Todo 2", done: false },
      ],
      currentTime: new Date(),
      address: ''
    };
  }

  componentDidMount() {
    this.timeInterval = setInterval(() => {
      this.setState({ currentTime: new Date() });
    }, 1000);
    this.getLocation();
  }

  componentWillUnmount() {
    clearInterval(this.timeInterval);
  }

  getLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(this.fetchLocationDetails, this.handleLocationError);
    } else {
      alert("Geolocation is not supported by this browser.");
    }
  }

  fetchLocationDetails = (position) => {
    const { latitude, longitude } = position.coords;
    console.log("Latitude:", latitude, "Longitude:", longitude); // 위치 정보 출력
    this.fetchAddress(latitude, longitude);
  }

  handleLocationError = (error) => {
    console.error("Error Code = " + error.code + " - " + error.message);
  }

  fetchAddress = async (latitude, longitude) => {
    const apiKey = 'AIzaSyCRQ9NdK-XhglCFeeAmd7XQFr2Af958O1g'; // 여기에 Google Maps API 키를 입력하세요.
    const url = `https://maps.googleapis.com/maps/api/geocode/json?latlng=${latitude},${longitude}&key=${apiKey}`;

    try {
      const response = await axios.get(url);
      const address = response.data.results[0]?.formatted_address;
      console.log("Address:", address); // 주소 정보 출력
      this.setState({ address });
    } catch (error) {
      console.error("Error fetching address:", error);
    }
  }

  add = (item) => {
    const newItem = { ...item, id: uuidv4(), done: false };
    const newItems = [...this.state.items, newItem];
    this.setState({ items: newItems });
    console.log("items:", newItems);
  }

  delete = (item) => {
    const newItems = this.state.items.filter(e => e.id !== item.id);
    this.setState({ items: newItems }, () => {
      console.log("Updated Items:", this.state.items);
    });
  }

  render() {
    const { items, currentTime, address } = this.state;

    const todoItems = items.length > 0 && (
      <Paper style={{ margin: 16 }}>
        <List>
          {items.map((item) => (
            <Todo item={item} key={item.id} delete={this.delete} />
          ))}
        </List>
      </Paper>
    );

    return (
      <div className='App'>
        <div className="black-nav">
          <h4>오늘의 할일</h4>
        </div>
        <Container maxWidth="md">
          <Grid container direction="column" alignItems="center" spacing={2} style={{ margin: '16px 0' }}>
            <Grid item>
              <Typography variant="h5" style={{ fontFamily: 'Roboto, Roboto', fontSize: '20px' }}>현재 시간: {currentTime.toLocaleTimeString()}</Typography>
            </Grid>
            <Grid item>
              <Typography variant="subtitle1" style={{ fontFamily: 'Arial, sans-serif', fontSize: '20px' }}>현재 위치: {address || 'Loading address...'}</Typography>
            </Grid>
          </Grid>
          <AddTodo add={this.add} />
          <div className='TodoList'>{todoItems}</div>
        </Container>
      </div>
    );
  }
}

export default App;
