import React from 'react';
import {ListItem,ListItemText, InputBase, Checkbox}from "@mui/material";

class Todo extends React.Component{
    constructor(props){
        super(props);
        this.state={item: props.item};
    }

    handleChechbokChange=() => {
        this.setState(prevState=> ({
            item: {...prevState.item, done: !prevState.item.done}
        }));
    };
    handleInputChange =(e) => {
        const {value } =e.target;
        this.setState(prevState => ({
            item: {...prevState.item, title:value}
        }));
    };
    render(){
        const {item} =this.state;
        return(
            <ListItem>
                <Checkbox checked={item.done} onChange={this.handleChechbokChange}/>
                <ListItemText>
                    <InputBase
                    inputProps={{"aria-label":"naked"}}
                    type="text"
                    id={item.id.toString()}
                    name={item.id.toString}
                    value={item.title}
                    onChange={this.handleInputChange}
                    multiline={true}
                    fullWidth={true}
                    />
                </ListItemText>
            </ListItem>
        );
    }
}


export default Todo;
