import React from 'react';
import { ListItem, ListItemText, InputBase, Checkbox, ListItemSecondaryAction, IconButton } from "@mui/material";
import DeleteOutlined from "@mui/icons-material/DeleteOutlined";

class Todo extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      item: props.item,
      readOnly: true,
      isSelected: false // 선택 상태 추가
    };
    this.delete = props.delete;
    this.update = props.update;
  }

  deleteEventHandler = () => {
    this.delete(this.state.item);
  }

  offReadOnlyMode = () => {
    this.setState({ readOnly: false});
  }

  enterKeyEventHandler = (e) => {
    if (e.key === "Enter") {
      this.setState({ readOnly: true });
      this.update(this.state.item);
    }
  }

  editEventHandler = (e) => {
    const thisItem = this.state.item;
    thisItem.title = e.target.value;
    this.setState({ item: thisItem });
  }

  checkboxEventHandler = (e) => { // 오타 수정
    console.log("check box event call");
    const thisItem = this.state.item;
    thisItem.done = !thisItem.done;
    this.setState({ item: thisItem });
    this.update(thisItem);
  }


 // 체크박스 이벤트 핸들러
 handleCheckboxChange = (event) => {
  const updatedItem = { ...this.props.item, done: event.target.checked };
  this.props.update(updatedItem);
};

 // 선택된 항목 삭제
  deleteSelectedItems = () => {
    call("/todo", "DELETE", { ids: this.state.selectedItems }).then(() => {
      this.loadTodos(); // 삭제 후 목록 새로고침
    });
  }


  render() {
    const { item, readOnly, isSelected } = this.state;
    return (
      <ListItem>
        <Checkbox
          checked={isSelected}
          onChange={this.handleCheckboxChange}
        />
        <ListItemText>
          <InputBase
            inputProps={{ "aria-label": "naked", readOnly: readOnly }}
            type="text"
            id={item.id}
            name={item.id}
            value={item.title}
            multiline={true}
            fullWidth={true}
            onClick={this.offReadOnlyMode}
            onChange={this.editEventHandler}
            onKeyPress={this.enterKeyEventHandler}
          />
        </ListItemText>

        <ListItemSecondaryAction>
          <IconButton aria-label="Delete" onClick={this.deleteEventHandler}>
            <DeleteOutlined />
          </IconButton>
        </ListItemSecondaryAction>
      </ListItem>
    );
  }
}

export default Todo;
