import Todo from './Todo';
import React from 'react';
import AddTodo from './AddTodo';
import { Paper, List, Container, Select, MenuItem, FormControl, InputLabel } from "@mui/material";
import './App.css';
import { v4 as uuidv4 } from 'uuid';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [
        { id: uuidv4(), title: "Todo 1", done: true },
        { id: uuidv4(), title: "Todo 2", done: false },
      ],
      filter: 'all',
    };
  }

  add = (item) => {
    const newItem = { ...item, id: uuidv4(), done: false };
    const newItems = [...this.state.items, newItem];
    this.setState({ items: newItems });
    console.log("items:", newItems);
  }

  delete = (item) => {
    const newItems = this.state.items.filter(e => e.id !== item.id);
    this.setState({ items: newItems }, () => {
      console.log("Updated Items:", this.state.items);
    });
  }

  handleFilterChange = (event) =>{
    this.setState({ filter: event.target.value});
  }

  render() {
    const { items, filter } = this.state;

    const filteredItems= items.filter(item =>{
      if (filter ==='done') return item.done;
      if (filter === 'not_done') return !item.done;
      return true;
    });

    const todoItems = filteredItems.length > 0 && (
      <Paper style={{ margin: 16 }}>
        <List>
          {items.map((item) => (
            <Todo item={item} key={item.id} delete={this.delete} />
          ))}
        </List>
      </Paper>
    );

    return (
      <div className='App'>
        <div className="black-nav">
          <h4>오늘의 할일</h4>
        </div>
        <Container maxWidth="md">
          <FormControl style={{ minWidth : 120, marginBottom: 16}}>

            <Select
              labelId="filter-label"
              value={this.state.filter}
              onChange={this.handleFilterChange}
            >
              <MenuItem value="all">All</MenuItem>
              <MenuItem value="done">Done</MenuItem>
              <MenuItem value="not_done">Not Done</MenuItem>
            </Select>
          </FormControl>
          <AddTodo add={this.add} />
          <div className='TodoList'>{todoItems}</div>
        </Container>
      </div>
    );
  }
}

export default App;
