import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import App from "./App";
import Login from "./Login";
import SignUp from "./SignUp";
import { isAuthenticated } from "./service/ApiService";

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {"저작권 "}
      fsoftwareengineer, {new Date().getFullYear()}
      {"."}
    </Typography>
  );
}

function AppRouter() {
  const [isAuth, setIsAuth] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkAuth = async () => {
        setLoading(true);
        try {
            const result = await fetch("/api/validate_token", { headers: { Authorization: `Bearer ${localStorage.getItem('ACCESS_TOKEN')}` }});
            if (result.ok) {
                setIsAuth(true);
            } else {
                setIsAuth(false);
            }
        } catch (error) {
            console.error('Error checking authentication', error);
            setIsAuth(false);
        }
        setLoading(false);
    };
    checkAuth();
}, []);
  if (loading) {
    return <Typography variant="h4" align="center">로딩중...</Typography>;
  }

  return (
    <Router>
      <div>
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route path="/signup" element={<SignUp />} />
          <Route path="/" element={isAuth ? <App /> : <Navigate replace to="/login" />} />
        </Routes>
      </div>
      <Box mt={5}>
        <Copyright />
      </Box>
    </Router>
  );
}

export default AppRouter;
