import React from 'react';
import Todo from './Todo';
import AddTodo from './AddTodo';
import { Paper, List, Container, Typography, Grid, Button } from "@mui/material";
import './App.css';
import { v4 as uuidv4 } from 'uuid';
import axios from 'axios';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [
        { id: uuidv4(), title: "Todo 1", done: true },
        { id: uuidv4(), title: "Todo 2", done: false },
      ],
      selectedItems: [],
      currentTime: new Date(),
      address: ''
    };
  }

  // 기타 코드

  toggleSelect = (item) => {
    const { selectedItems } = this.state;
    const updatedSelectedItems = selectedItems.includes(item.id)
      ? selectedItems.filter(id => id !== item.id)
      : [...selectedItems, item.id];
    this.setState({ selectedItems: updatedSelectedItems });
  }

  deleteSelected = () => {
    const { items, selectedItems } = this.state;
    const updatedItems = items.filter(item => !selectedItems.includes(item.id));
    this.setState({ items: updatedItems, selectedItems: [] });
  }

  render() {
    const { items, currentTime, address, selectedItems } = this.state;
    const todoItems = items.length > 0 && (
      <Paper style={{ margin: 16 }}>
        <List>
          {items.map((item) => (
            <Todo
              key={item.id}
              item={item}
              delete={this.delete}
              toggleSelect={this.toggleSelect}
              isSelected={selectedItems.includes(item.id)}
            />
          ))}
        </List>
      </Paper>
    );

    return (
      <div className='App'>
        <div className="black-nav">
          <h4>오늘의 할일</h4>
        </div>
        <Container maxWidth="md">
          {/* 기타 코드 */}
          <Button
            variant="contained"
            color="secondary"
            onClick={this.deleteSelected}
            disabled={selectedItems.length === 0}
            style={{ marginBottom: 16 }}
          >
            선택된 항목 삭제
          </Button>
          {/* 기타 코드 */}
        </Container>
      </div>
    );
  }
}

export default App;
