import React from 'react';
import Todo from './Todo';
import AddTodo from './AddTodo';
import { Paper, List, Container, Typography, Grid } from "@mui/material";
import './App.css';
import { v4 as uuidv4 } from 'uuid';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [
        { id: uuidv4(), title: "Todo 1", done: true },
        { id: uuidv4(), title: "Todo 2", done: false },
      ],
      currentTime: new Date(),
      address: '서울특별시 강남구 테헤란로 427', // 예시 도로명 주소
      weather: '맑음 25°C' // 예시 날씨 정보
    };
  }

  componentDidMount() {
    this.timeInterval = setInterval(() => {
      this.setState({ currentTime: new Date() });
    }, 1000);
    this.fetchWeather();
  }

  componentWillUnmount() {
    clearInterval(this.timeInterval);
  }

  fetchWeather = () => {
    // 여기에 실제 날씨 API 호출 코드 추가
    // 예시: fetch('API_URL').then(response => response.json()).then(data => this.setState({ weather: data.weather }));
  }

  add = (item) => {
    const newItem = { ...item, id: uuidv4(), done: false };
    const newItems = [...this.state.items, newItem];
    this.setState({ items: newItems });
    console.log("items:", newItems);
  }

  delete = (item) => {
    const newItems = this.state.items.filter(e => e.id !== item.id);
    this.setState({ items: newItems }, () => {
      console.log("Updated Items:", this.state.items);
    });
  }

  render() {
    const { items, currentTime, address, weather } = this.state;

    const todoItems = items.length > 0 && (
      <Paper style={{ margin: 16 }}>
        <List>
          {items.map((item) => (
            <Todo item={item} key={item.id} delete={this.delete} />
          ))}
        </List>
      </Paper>
    );

    return (
      <div className='App'>
        <div className="black-nav">
          <h4>오늘의 할일</h4>
        </div>
        <Container maxWidth="md">
          <Grid container direction="column" alignItems="center" spacing={2} style={{ margin: '16px 0' }}>
            <Grid item>
              <Typography variant="h6">{currentTime.toLocaleTimeString()}</Typography>
            </Grid>
            <Grid item>
              <Typography variant="subtitle1">{address}</Typography>
            </Grid>
            <Grid item>
              <Typography variant="subtitle2">{weather}</Typography>
            </Grid>
          </Grid>
          <AddTodo add={this.add} />
          <div className='TodoList'>{todoItems}</div>
        </Container>
      </div>
    );
  }
}

export default App;
