import Todo from './Todo';
import React from 'react';
import AddTodo from './AddTodo';
import {Paper, List, Contrainer}from "@mui/material";
import './App.css';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state={
      item :[
        {id:0, title:"Todo 1", done:true},
        {id:1, title:"Todo 2", done:false},
      ],
    };
  }
  add =(item) =>{
    const thisItems=this.state.itmes;
    item.id="ID-"+thisItems.length;
    item.done=false;
    thisItems.push(item);
    this.setState({items:thisItems});
    console.log("items:",this.state.item);

  }
  render(){
    const {item} = this.state;

    const todoItems =item.length >0 &&(
      <Paper style={{margin:16}}>
        <List>
          {item.map((item,idx)=>(
            <Todo item={item} key={item.id}/>
          ))}
        </List>
      </Paper>
    );
    return (
      <div className='App'>
        <Contrainer maxWidth="md">
          <AddTodo add={this.add}/>
          <div className='TodoList'>{todoItems}</div>
        </Contrainer>
      </div>
    );
  }
}

export default App;
