import React from 'react';
import Todo from './Todo';
import AddTodo from './AddTodo';
import { Paper, List, Container, Typography, Grid, Button } from "@mui/material";
import { ArrowBack, ArrowForward } from "@mui/icons-material";
import './App.css';


import { call, signout, isAuthenticated } from './service/ApiService';

const clockLogo = require('./free-icon-clock-98170.png');
const locationLogo = require('./free-icon-location-4874744.png');

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [
        { id: uuidv4(), title: "Todo 1", done: true, selected: false },
        { id: uuidv4(), title: "Todo 2", done: false, selected: false },
      ],
      currentTime: new Date(),
      address: '', 
      loading: true,
      page: 0,
      totalPages: 0,
      startPage: 0,
      endPage: 0,
    };
  }

  componentDidMount() {
    if (isAuthenticated()) {
      this.loadTodos(0);
    } else {
      this.setState({ loading: false });
    }

    this.timeInterval = setInterval(() => {
      this.setState({ currentTime: new Date() });
    }, 1000);
    this.getLocation();


  }

  componentWillUnmount() {
    clearInterval(this.timeInterval);
  }

  loadTodos = (page) => {
    this.setState({ loading: true });
    call(`/todo/page?page=${page}&size=3`, "GET", null).then((response) => {
      this.setState({ 
        items: response.todoPage.content,
        loading: false,
        page: response.todoPage.number,
        totalPages: response.todoPage.totalPages,
        startPage: response.startPage,
        endPage: response.endPage,
      });
    }).catch((error) => {
      console.error("로딩 실패", error);
      this.setState({ loading: false });
    });
  }


  getLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(this.fetchLocationDetails, this.handleLocationError);
    } else {
      alert("Geolocation is not supported by this browser.");
    }
  }

  fetchLocationDetails = (position) => {
    const { latitude, longitude } = position.coords;
    console.log("Latitude:", latitude, "Longitude:", longitude); // 위치 정보 출력
    this.fetchAddress(latitude, longitude);
  }

  handleLocationError = (error) => {
    console.error("Error Code = " + error.code + " - " + error.message);
  }


  fetchAddress = async (latitude, longitude) => {
    const apiKey = 'AIzaSyCRQ9NdK-XhglCFeeAmd7XQFr2Af958O1g'; // 여기에 Google Maps API 키를 입력하세요.
    const url = `https://maps.googleapis.com/maps/api/geocode/json?latlng=${latitude},${longitude}&key=${apiKey}`;

    try {
      const response = await axios.get(url);
      const address = response.data.results[0]?.formatted_address;
      console.log("Address:", address); // 주소 정보 출력
      this.setState({ address });
    } catch (error) {
      console.error("Error fetching address:", error);
    }
  }


  /*


  add = (item) => {
    const newItem = { ...item, id: uuidv4(), done: false, selected: false };
    const newItems = [...this.state.items, newItem];
    this.setState({ items: newItems });
    console.log("items:", newItems);
  }

  delete = (item) => {
    const newItems = this.state.items.filter(e => e.id !== item.id);
    this.setState({ items: newItems }, () => {
      console.log("Updated Items:", this.state.items);
    });
  }


  */


  add = (item) => {
    call("/todo", "POST", item).then((response) => {
      this.loadTodos(0);
    });
  }

  delete = (item) => {
    call("/todo", "DELETE", item).then((response) => {
      this.setState({ page: 0 }, () => {
        this.loadTodos(0);
      });
    });
  }


  update = (item) => {
    call("/todo", "PUT", item).then((response) => {
      this.loadTodos(this.state.page);
    }).catch((error) => {
      console.error("업데이트 실패", error);
    });
  }


  deleteAll = () => {
    console.log("전체 삭제 버튼 클릭됨");
    call("/todo/all", "DELETE").then((response) => {
      console.log(response);
      console.log("전체 삭제 성공");
      this.setState({ items: [], page: 0, totalPages: 0 });
    }).catch((error) => {
      console.error("전체 삭제 실패", error);
    });
  }


  toggleSelect = (item) => {
    const newItems = this.state.items.map(e => 
      e.id === item.id ? { ...e, selected: !e.selected } : e
    );
    this.setState({ items: newItems });
  }

  deleteSelected = () => {
    const newItems = this.state.items.filter(e => !e.selected);
    this.setState({ items: newItems });
  }



  handlePrevPage = () => {
    if (this.state.page > 0) {
      this.loadTodos(this.state.page - 1);
    }
  }

  handleNextPage = () => {
    if (this.state.page < this.state.totalPages - 1) {
      this.loadTodos(this.state.page + 1);
    }
  }



  render() {
    const { items, loading, currentTime, address, page, totalPages } = this.state;

    if (loading) {
      return <Typography variant="h1">로딩중...</Typography>;
    }

    const todoItems = items.length > 0 && (
      <Paper style={{ margin: 16 }}>
        <List>
          {items.map((item) => (
            <Todo item={item} key={item.id} delete={this.delete} toggleSelect={this.toggleSelect} update={this.update} />
          ))}
        </List>
      </Paper>
    );

    const navigationBar = (
      <AppBar position="static">
        <Toolbar>
          <Grid container justifyContent="space-between">
            <Typography variant="h6" component="div">오늘의 할일</Typography>
            <Button color="inherit" onClick={this.signout}>Logout</Button>
          </Grid>
        </Toolbar>
      </AppBar>
    );

    return (
      <div className='App'>
        {navigationBar}
        <Container maxWidth="md">
          <Grid container direction="column" alignItems="center" spacing={2} style={{ margin: '16px 0' }}>
            <Grid item container alignItems="center">
              <img src={clockLogo} alt="Clock Logo" style={{ width: 30, height: 30, marginRight: 8 }} />
              <Typography variant="h5">현재 시간: {currentTime.toLocaleTimeString()}</Typography>
            </Grid>
            <Grid item container alignItems="center">
              <img src={locationLogo} alt="Location Logo" style={{ width: 30, height: 30, marginRight: 8 }} />
              <Typography variant="subtitle1">현재 위치: {address || 'Loading address...'}</Typography>
            </Grid>
          </Grid>
          <AddTodo add={this.add} />
          <div className='TodoList'>{todoItems}</div>
          <Button variant="contained" color="secondary" onClick={this.deleteSelected} style={{ margin: '16px 0' }}>
            선택 항목 삭제
          </Button>
          <Grid container justifyContent="center" style={{ marginTop: 16 }}>
            <IconButton onClick={this.handlePrevPage} disabled={page === 0}>
              <ArrowBack />
            </IconButton>
            <Typography variant="body2" style={{ display: 'inline', margin: '0 16px' }}>
              Page {page + 1} of {totalPages}
            </Typography>
            <IconButton onClick={this.handleNextPage} disabled={page >= totalPages - 1}>
              <ArrowForward />
            </IconButton>
          </Grid>
        </Container>
      </div>
    );
  }
}

export default App;
